mapper.select:
SELECT * FROM follow_up


        and follow_up_name = #{followUpName}


        and follow_up_code = #{followUpCode}


        and follow_up_backdrop = #{followUpBackdrop}

SELECT f.*,hdc.hospital_department_child_name,hdc.hospital_department_child_id as hdcID FROM follow_up f,hospital_department_child hdc WHERE f.hospital_department_child_code = hdc.hospital_department_child_code
and f.follow_up_principal like CONCAT ("%",#{followUpPrincipal},"%")
and hdc.hospital_department_child_name LIKE CONCAT ("%",#{hospitalDepartmentChildName},"%")
and f.follow_up_status = #{followUpStatus}
and f.follow_up_create_time between #{followUpCreateTime1} and #{followUpCreateTime2}

select 'true' as QUERYID,

from follow_up
where follow_up_id = #{followUpId,jdbcType=INTEGER}
